  //     `https://api.github.com/users?client_id=${clientID}&client_secret=${clientSecert}`
  //   );
  //   this.setState({ users: res.data, loading: false });

  //   console.log(res.data);
  // }
  serachUserInGit = async text => {
    var clientID = 'cc14c52960a3ea8b1cb7';
    var clientSecert = 'a9677e62446a04526d52012c2ed1d3564746abc1';
    const res = await axios.get(
      `https://api.github.com/search/users?q=${text}?client_id=${clientID}&client_secret=${clientSecert}`
    );
    this.setState({ users: res.data.items, loading: false });

    console.log(res.data.items);
  };
  render() {
    return (
      <div className="App">
        <Navbar />
        <div className="container"></div>
        <Serach serachUser={this.serachUserInGit} />
        <Users loading={this.state.loading} users={this.state.users} />
      </div>
    );
  }
}

export default App;
