import React, { Component,Fragment } from 'react';
import { Redirect } from 'react-router'


class ShowPlayers extends Component {


  constructor(props){
    super(props)
    if(!this.props.location.state)
      return
    this.state = {
      players : this.props.location.state.playersInGame,
    }
  }
  // static getDerivedStateFromProps(props,state) {
  //   if(!state.change)
  //   return false
  // }
  componentDidCatch(error, info) {

   this.setState({change: true,players: this.props.location.state.playersInGame})
  }

  render() {
    if(!this.state)
    {
      return <div>Error</div>
    }
    console.log(this.state.data)
    const { data } = this.state
    const players = data.filter(elm => elm["Team or Player"] === "Player");

    return (
      <Fragment>
        <table>
        <thead>
        <th>Player Name</th>
          <th>Last Name</th>
          <th>Total Time</th>
          <th>Distance (km)</th>
          <th>Sprint Distance</th>
          <th> Total Sprints</th>
          <th>Sprints Avg(MP/H).</th>
        </thead>
      {players.map((player) => (
        <tbody>
        <tr>
          <td> {player["First Name"]}</td>
          <td> {player.lastname}</td>
          <td> {player.total_time}</td>
          <td> {player.total_distance}</td>
          <td> {player.sprint_distance}</td>
          <td> {player.total_sprint}</td>
          <td> {player.sprint_avg}</td>
        </tr>


        </tbody>
      ))}
        </table>
      </Fragment>
    )
  }
}
export default ShowPlayers;
