import React from 'react';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import Serach from './components/users/Serach';

import axios from 'axios';
import './App.css';

class App extends React.Component {
  state = {
    users: [],
    loading: false,
  };
  async componentDidMount() {
    let clientID = 'cc14c52960a3ea8b1cb7';
    let clientSecert = 'a9677e62446a04526d52012c2ed1d3564746abc1';
    this.setState({ loading: true });
    const res = await axios.get(
      `https://api.github.com/users?client_id=${clientID}&client_secret=${clientSecert}`
    );
    this.setState({ users: res.data, loading: false });

    console.log(res.data);
  }
  serachUserInGit = async text => {
    const res = await axios.get(
      `https://api.github.com/search/users?q=${text}users?client_id=${clientID}&client_secret=${clientSecert}`
    );
    this.setState({ users: res.data.item, loading: false });

    console.log(res.data);
  };
  render() {
    return (
      <div className="App">
        <Navbar />
        <div className="container"></div>
        <Serach serachUser={this.serachUserInGit} />
        <Users loading={this.state.loading} users={this.state.users} />
      </div>
    );
  }
}

export default App;
