import React from 'react';
import "../css/CreateMatch.css"

const CreateMatch = (props) => {
  const myTeam = 'Hapiol Patch Tikvwa'


  function checkValidNumber(number) {
    if(number>10 || number<0) {
      alert("ERROR!")
    }

  }


  return (
    <div>
    <h1>Create Match</h1>
  <label >
  <table  style={{width: '100%'}}>
    <tr>
    <td>Teams</td>
      <td> {myTeam}</td>
      <td> VS </td>
      <td> <input type="text"  size="8"></input> </td>
    </tr>
    <tr>
      <td>Half Score</td>
      <td> <input type="number" min="0" max="10"></input></td>
      <td> - </td>
      <td> <input type="number" min="0" max="10"></input> </td>
    </tr>
    <tr>
      <td>Final Score</td>
      <td> <input type="number" min="0" max="10" onChange={(e)=> checkValidNumber(e.target.value)}></input> </td>
      <td> - </td>
      <td> <input type="number" min="0" max="10" ></input> </td>
    </tr>
  </table>
  </label>
    </div>
  );
};
export default CreateMatch;
