import React, { Fragment } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import Serach from './components/users/Serach';
import Alert from './components/layout/Alert';
import About from './components/Pages/About';
import axios from 'axios';
import './App.css';

class App extends React.Component {
  state = {
    users: [],
    loading: false,
    alert: null,
  };

  //Search Github users
  serachUserInGit = async text => {
    this.setState({ loading: true });
    var clientID = 'cc14c52960a3ea8b1cb7';
    var clientSecert = 'a9677e62446a04526d52012c2ed1d3564746abc1';
    const res = await axios.get(
      `https://api.github.com/search/users?q=${text}&client_id=${clientID}&client_secret=${clientSecert}`
    );
    this.setState({ users: res.data.items, loading: false });
  };
  //clear user from state
  clearUsers = () => this.setState({ users: [], loading: false });

  //Set Alert
  setAlert = (msg, type) => {
    this.setState({ alert: { msg, type } });
    setTimeout(() => this.setState({ alert: null }), 5000);
  };

  render() {
    const { users, loading } = this.state;
    return (
      <Router>
        <div className="App">
          <Navbar />
          <div className="container">
            <Alert alert={this.state.alert} />
            <Switch>
              <Route
                exact
                path="/"
                render={props => (
                  <Fragment>
                    <Serach
                      serachUser={this.serachUserInGit}
                      clearUsers={this.clearUsers}
                      showClear={users.length > 0 ? true : false}
                      setAlert={this.setAlert}
                    />
                  </Fragment>
                )}
              />
              <Route exact path="/About" component={About} />
            </Switch>

            <Users loading={loading} users={users} />
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
