import React, { Component,Fragment } from 'react';
import { Redirect } from 'react-router'
import '../css/ShowPlayers.css'

class ShowPlayers extends Component {


  constructor(props){
    super(props)
    if(!this.props.location.state)
      return
    this.state = {
      players : this.props.location.state.playersInGame,

    }
  }


  render() {
    if(!this.state)
    {       
      return <Redirect to={{
        pathname: '/',
    }}
/>
    }
    var { players } = this.state
    players = players.filter(elm => elm["Team or Player"] === "Player");
    console.log(players)

    return (
      <Fragment>
        <table>
        <thead>
        <th>Player Name</th>
          <th>Last Name</th>
          <th>Total Time</th>
          <th>Distance (km)</th>
          <th>Sprint Distance</th>
          <th> Total Sprints</th>
          <th>Sprints Avg(MP/H).</th>
        </thead>
      {players.map((player) => (
        <tbody>
        <tr >
          <td class="tooltip" >{ player["First Name"]}</td>
          <td class="tooltip" > {player["Last Name"]}</td>
          <td class="tooltip" > {player["Time on Pitch (mins)"]}</td>
          <td class="tooltip" > {player["Distance (km)"]} <span class="tooltiptext" > Half(1): {player["Distance 1st Half (km)"] } Half(2) {player["Distance 2nd Half (km)"]}</span></td>  
          <td class="tooltip" > {player["Progressive Sprints"]}</td>
          <td class="tooltip" > {player["Sprints"]}</td>
          <td class="tooltip" > {player["Top Speed (km/h)"]}</td>
        </tr>


        </tbody>
      ))}
        </table>
      </Fragment>
    )
  }
}
export default ShowPlayers;
