import React from 'react'
import {Paper, Card , Typography , makeStyles, Button} from '@material-ui/core'

const useStyle = makeStyles(theme => ({
    root: {
        backgroundColor: '#fdfdff'
    },
    PageHeader: {
        padding: theme.spacing(4),
        display: 'block-inline',
        marginBottom: theme.spacing(3),
        backgroundColor: '#fdfdff',
        paddingBlock: '10px',
        margin: '5px',
        borderLeft: '6px'
    },
    pageIcon: {
        display: 'inline-block',
        padding: theme.spacing(4),
        color: '#3c44b1', 
    },
    pageTittle: {
        paddingLeft: theme.spacing(4)
    }
}))


export default function PageHeader(props) {

    const classes = useStyle()
    const { title , icon } = props

    function clicked(e) {
        console.log(e)
    }
    return (
        <Button onClick={ () => clicked(title)} className={classes.PageHeader} size="large" color="primary" >
          <Paper elevation={0} square className={classes.root}>
            <div  className={classes.PageHeader}>
                <Card className={classes.pageIcon}>
                    {icon}
                    </Card>
                    <div className={classes.pageTittle}>
                        <Typography paddingBlock="10px" variant="h4" component="span">
                        {title}
                        </Typography>
                     </div>
             </div>

        </Paper> 
        </Button>






    )
}
