import React, { Fragment, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import User from './components/users/User';
import Serach from './components/users/Serach';
import Alert from './components/layout/Alert';
import About from './components/Pages/About';
import axios from 'axios';
import './App.css';

const App = () => {
  const [users, setUsers] = setState([]);
  const [user, setUser] = setState({});
  const [repos, setRepos] = setState([]);
  const [loading, setLoading] = setState(false);
  const [alert, setAlert] = setState(null);

  //Search Github users
  const serachUserInGit = async text => {
    setLoading(true);
    var clientID = 'cc14c52960a3ea8b1cb7';
    var clientSecert = 'a9677e62446a04526d52012c2ed1d3564746abc1';
    const res = await axios.get(
      `https://api.github.com/search/users?q=${text}&client_id=${clientID}&client_secret=${clientSecert}`
    );
    setUsers(res.data.items);
    setLoading(false);
  };
  //clear user from state
  const clearUsers = () => {
    setUsers([]);
    setLoading(false);
  };

  // Get single Github user
  const getUser = async username => {
    setLoading(true);
    var clientID = 'cc14c52960a3ea8b1cb7';
    var clientSecert = 'a9677e62446a04526d52012c2ed1d3564746abc1';
    console.log('try get Data from userGit');

    const res = await axios.get(
      `https://api.github.com/users/${username}?client_id=${clientID}&client_secret=${clientSecert}`
    );
    setUser(res.data);

    setLoading(false);
  };

  // Get users repos
  const getUserRepos = async username => {
    setLoading(true);
    var clientID = 'cc14c52960a3ea8b1cb7';
    var clientSecert = 'a9677e62446a04526d52012c2ed1d3564746abc1';
    console.log('try get Data from userGit');

    const res = await axios.get(
      `https://api.github.com/users/${username}/repos?per_page=5&sort=created:as&client_id=${clientID}&client_secret=${clientSecert}`
    );
    setRepos(res.data);
    setLoading(false);
  };
  //Set Alert
  const showAlert = (msg, type) => {
    setAlert({ msg, type });
    setTimeout(() => setAlert(null), 5000);
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <div className="container">
          <Alert alert={alert} />
          <Switch>
            <Route
              exact
              path="/"
              render={props => (
                <Fragment>
                  <Serach
                    serachUser={serachUserInGit}
                    clearUsers={clearUsers}
                    showClear={users.length > 0 ? true : false}
                    showAlert={showAlert}
                  />
                  <Users loading={loading} users={users} />
                </Fragment>
              )}
            />
            <Route exact path="/About" component={About} />
            <Route
              exact
              path="/user/:login"
              render={props => (
                <User
                  {...props}
                  getUser={getUser} //Apply func
                  getUserRepos={getUserRepos} //Apply func
                  repos={repos} //Send props
                  user={user} //Send props
                  loading={loading} //Send props
                />
              )}
            />
          </Switch>
        </div>
      </div>
    </Router>
  );
};

export default App;
