import React, { Component } from 'react'

import { NavLink } from 'react-router-dom';



export default class Navbar extends Component {
  constructor(props) {
    super(props)
    this.state = {
        title: 'DeskAnalyst System',
        icon: 'fa fa-futbol',
        teamName: null
    }
  }

  componentDidMount() {
    const user = JSON.parse(sessionStorage.getItem("loginUser"))
    if(user) 
      this.setState({teamName : user.teamName})
}
  render() {

    const { icon , teamName ,title} = this.state
    return (
      <nav className="navbar bg-primary">
        <h1>
          <i className={icon}  /> {title} 
        </h1>
         {this.state.teamName? <h2> My Team: {teamName} </h2> : "" }
        <ul>
          <li>
            <NavLink to="/Dashboard" exact activeStyle={{color:'blue'}}>Home </NavLink>
          </li>
          <li>
            <NavLink to="/TeamSqoud" exact activeStyle={{color:'blue'}}>Team Sqoud</NavLink>
          </li>
          <li>
            <NavLink to="/Match" exact activeStyle={{color:'blue'}}>Match </NavLink>
          </li>
          <li>
            <NavLink to="/CreateMatch" exact activeStyle={{color:'blue'}}>Create-Match </NavLink>
          </li>
        </ul>
      </nav>
    );
  };
}
