import React, { Component,Fragment } from 'react';
import { Redirect } from 'react-router'


class ShowPlayers extends Component {


  constructor(props){
    super(props)
    if(!this.props.location.state)
      return
    this.state = {
      players : this.props.location.state.playersInGame,

    }
  }
  // static getDerivedStateFromProps(props,state) {
  //   if(!state.change)
  //   return false
  // }
  componentDidCatch(error, info) {

   this.setState({change: true,players: this.props.location.state.playersInGame})
  }

  render() {
    if(!this.state)
    {
      return <div>Error</div>
    }
    var { players } = this.state
    players = players.filter(elm => elm["Team or Player"] === "Player");
    console.log(players)

    return (
      <Fragment>
        <table>
        <thead>
        <th>Player Name</th>
          <th>Last Name</th>
          <th>Total Time</th>
          <th>Distance (km)</th>
          <th>Sprint Distance</th>
          <th> Total Sprints</th>
          <th>Sprints Avg(MP/H).</th>
        </thead>
      {players.map((player) => (
        <tbody>
        <tr>
          <td> {player["First Name"]}</td>
          <td> {player["Last Name"]}</td>
          <td> {player["Time on Pitch (mins)"]}</td>
          <td> {player["Distance (km)"]}</td>
          <td> {player["Progressive Sprints"]}</td>
          <td> {player["Sprints"]}</td>
          <td> {player["Top Speed (km/h)"]}</td>
        </tr>


        </tbody>
      ))}
        </table>
      </Fragment>
    )
  }
}
export default ShowPlayers;
